'use client'

import React, { useEffect, useCallback, useState, useRef } from 'react'
import CountUp from 'react-countup'
import { InView } from 'react-intersection-observer'

import Title from '@/components/common/Title'
import helpers from '@/lib/helpers'
import styles from './style.module.scss'

import { useSocket } from '@/logic/socket/useSocket'

const StatItem = React.memo(function StatItem({ title, icon: Icon, value, formatFn }) {
  const [displayedValue, setDisplayedValue] = useState(value)
  const prevValueRef = useRef(value)

  useEffect(() => {
    if (value !== displayedValue) {
      prevValueRef.current = displayedValue
      setDisplayedValue(value)
    }
    // eslint-disable-next-line
  }, [value])

  const start = prevValueRef.current
  const end = displayedValue
  const shouldAnimate = start !== end

  return (
    <div className={styles.itemWrapper}>
      <div className={styles.item}>
        <Title type={3} size={5}>{title}</Title>
        <InView triggerOnce threshold={1}>
          {({ ref, inView }) =>
            inView ? (
              <span ref={ref}>
                {shouldAnimate ? (
                  <CountUp
                    start={start}
                    end={end}
                    duration={2.5}
                    delay={0.01}
                    formattingFn={formatFn}
                  />
                ) : (
                  formatFn(end)
                )}
              </span>
            ) : (
              <span ref={ref}>{formatFn(0)}</span>
            )
          }
        </InView>
        {Icon && <Icon />}
      </div>
    </div>
  )
});

export default function WidgetStats() {
  const socketClient = useSocket(process.env.NEXT_PUBLIC_DASHBOARD_SOCKET_URI)

  const [totalWalletsConnected, setTotalWalletsConnected] = useState(0)
  const [totalContextsSaved, setTotalContextsSaved] = useState(0)
  const [totalContextBadgesClaimed, setContextBadgesClaimed] = useState(0)
  const [totalMissionsCompleted, setTotalMissionsCompleted] = useState(0)

  const formatNumber = useCallback((num) => helpers.formatNumber(num, 2), [])

  useEffect(() => {
    if (!socketClient) return

    const updateIfGreater = (setter, prevValue) => (data) => {
      if (data?.total > 0 && data.total > prevValue) {
        setter(data.total)
      }
    }

    socketClient.on('WalletsConnected', updateIfGreater(setTotalWalletsConnected, totalWalletsConnected))
    socketClient.on('TotalContextsSaved', updateIfGreater(setTotalContextsSaved, totalContextsSaved))
    socketClient.on('ContextBadgesClaimed', updateIfGreater(setContextBadgesClaimed, totalContextBadgesClaimed))
    socketClient.on('TotalMissionsCompleted', updateIfGreater(setTotalMissionsCompleted, totalMissionsCompleted))
  }, [socketClient])

  return (
    <div className={styles.grid}>
      <StatItem
        title="Wallets Connected"
        icon={IconUsers}
        value={totalWalletsConnected}
        formatFn={formatNumber}
      />
      <StatItem
        title="Total Contexts Saved"
        icon={IconBlocks}
        value={totalContextsSaved}
        formatFn={formatNumber}
      />
      <StatItem
        title="Context Badges Claimed"
        icon={IconTXs}
        value={totalContextBadgesClaimed}
        formatFn={formatNumber}
      />
      <StatItem
        title="Total Missions Completed"
        icon={IconTransfers}
        value={totalMissionsCompleted}
        formatFn={formatNumber}
      />
    </div>
  )
}

